import React, { useState, useEffect } from 'react';
import './style.css';
import api from './services/api';


function Livro() {
    const [livros, setLivros] = useState([]);

    useEffect(() => {
        api.get('books',{}).then(response => {
            setLivros(response.data);
        });
    }, []);

    return (
        <div>
            <h1>Livros</h1>
            <ul>
                <div> Fazer o cadastro
                <br></br>
            Nome:<input name='Nome'/>
            Editora:<input name='editora'/>
            isbn:<input name='isbn'/>
            <button type="button" onClick={() =>salvarLivro(livro.idlivro)}>salvar</button>
            
            </div>

                {
                    livros.map(livro => (
                        <tr>
                            <td>Id: {livro.idlivro}</td>
                            <td>Nome: {livro.nome}</td>
                            <td>editora: {livro.editora}</td>
                            <td>isbn: {livro.isbn}</td>
                        </tr>
                    ))
                }
            </ul>
        </div>
    );
}

export default Livro;
